package userinput;

import java.util.Scanner;

public class string {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
       Scanner sc=new Scanner(System.in);
       while(true)
       {
    	   String a=sc.nextLine();
    	   String b=sc.nextLine();
    	   System.out.println(a+b);
       }
	}

}
